typedef enum gnutls_certificate_verify_flags { /* If set a signer does not have to be a certificate authority. This * flag should normaly be disabled, unless you know what this means. */ GNUTLS_VERIFY_DISABLE_CA_SIGN = 1,
/* Allow only trusted CA certificates that have version 1. This is * safer than GNUTLS_VERIFY_ALLOW_ANY_X509_V1_CA_CRT, and should be * used instead. That way only signers in your trusted list will be * allowed to have certificates of version 1. */ GNUTLS_VERIFY_ALLOW_X509_V1_CA_CRT = 2,
/* If a certificate is not signed by anyone trusted but exists in * the trusted CA list do not treat it as trusted. */ GNUTLS_VERIFY_DO_NOT_ALLOW_SAME = 4,
/* Allow CA certificates that have version 1 (both root and * intermediate). This might be dangerous since those haven't the * basicConstraints extension. Must be used in combination with * GNUTLS_VERIFY_ALLOW_X509_V1_CA_CRT. */ GNUTLS_VERIFY_ALLOW_ANY_X509_V1_CA_CRT = 8,
/* Allow certificates to be signed using the broken MD2 algorithm. */ GNUTLS_VERIFY_ALLOW_SIGN_RSA_MD2 = 16,
/* Allow certificates to be signed using the broken MD5 algorithm. */ GNUTLS_VERIFY_ALLOW_SIGN_RSA_MD5 = 32,
/* Disable checking of activation and expiration validity * periods of certificate chains. Don't set this unless you * understand the security implications. */ GNUTLS_VERIFY_DISABLE_TIME_CHECKS = 64 } gnutls_certificate_verify_flags;

